import React, {useEffect} from 'react';

import "./index.css"

const Tracks = (props) => {
   const addTrack = useEffect(
    (event) => {
        props.onAdd(props.tracks)
    }, [props.onAdd, props.tracks]);

    const removeTrack = useEffect(
        (event) => {
            props.onRemove(props.tracks)
        }, [props.onRemove, props.tracks]
    );

    const renderAction = () => {
        if(props.isRemoval) {
          return (
            <button className="trackRemove" onClick={removeTrack}> 
             -
            </button>
          )
        }

        return (
            <button className="trackAdd" onClick={addTrack}>
                +
            </button>
        )

         
    };

    return (
        <div className="Tracks">
            <div className="track-Info">
                <h3> {props.track.name} </h3>
                <p> 
                 {props.tracks.artist} || {props.tracks.album}
                </p>
                {renderAction()}
            </div>
        </div>
    )


}

export default Tracks;
